import Emailer from 'components/users/emailer'

export default {
  props: ['item'],
  template: require('./template'),

  data: () => ({
    email: null
  }),

  components: {
    Emailer
  },

  methods: {

    prepare() {
      this.email = 'agent_welcome'
    },

    send(subject, body) {
      this.api.resend_welcome_email(this.item.id, subject, body).send({
        success: () => {
          this.close()
        }
      })
    },

    close() {
      this.email = null
    }
  }
}
