class EmailLayouts < BaseController
  include Crudle::Controller

  private
  def model; EmailLayout end

  def order; :name end
  
  def name_filter params, dataset
    return if params[:name].nil? || params[:name].empty?
    dataset.where('name ILIKE ?', '%' << params[:name] << '%')
  end

