import c3 from 'c3'

export default {
  props: ['data'],
  template: require('./template'),

  mounted() {
    this.$nextTick(() => {
      c3.generate({
        bindto: this.$el.querySelector('#chart'),
        data: {
          columns: [
            ['Men', this.data.male],
            ['Women', this.data.female]
          ],
          type: 'pie'
        },
        pie: {
          label: {
            format: (val) => val
          }
        }
      })
    })
  }
}
