import c3 from 'c3'

export default {
  props: ['data'],
  template: require('./template'),

  mounted() {
    this.$nextTick(() => {
      c3.generate({
        bindto: this.$el.querySelector('#chart'),
        data: {
          columns: [
            ['Applications'].concat(this.data.applications),
            ['Declined'].concat(this.data.rejected),
            ['Approved'].concat(this.data.approved),
          ]
        },
        axis: {
          x: {
            type: 'category',
            categories: this.data.months
          },
          y: { show: false }
        }
      })
    })
  }
}
