import Alert from 'appril-alert'

import Editor from '../editor'

export default {
  props: ['profile', 'label'],
  template: require('./template'),

  components: {
    Editor
  },

  methods: {
    approve() {
      Alert.confirm("You are about to APPROVE this profile!", () => {
        this.api.approve(this.profile.id).send({
          success: (profile) => {
            this.$store.commit('CRUDLE__UPDATE_ITEM', profile)
          }
        })
      })
    }
  }
}
