import {height_format} from 'helpers/application_helpers'
import {render_gallery} from './methods'

import TypedSpec from './typed-spec'
import OptedSpec from './opted-spec'
import Photos from './photos'

export default {
  props: ['profile'],
  template: require('./template'),

  methods: {
    height_format,
    render_gallery,

    sport(sport) {
      return (this.$options.specs.sports.opts || {})[sport] || sport
    },

    genre(genre) {
      return (this.$options.specs.genres.opts || {})[genre] || genre
    }
  },

  components: {
    TypedSpec,
    OptedSpec,
    Photos
  },

  computed: {
    sports() {
      return (this.profile.sports || []).concat(this.profile.other_sports || [])
    },

    genres() {
      return (this.profile.genres || []).concat(this.profile.other_genres || [])
    }
  }
}
