import Alert from 'appril-alert'
import {render_gallery} from '../methods'

export default {
  props: ['profile'],
  template: require('./template'),

  methods: {
    render_gallery,

    delete_photo(photo_id, index) {
      Alert.confirm(() => {
        this.api.delete_photo(this.profile.id, photo_id).send({
          success: () => {
            this.$store.commit('MD__DELETE_PHOTO', index)
          }
        })
      })
    }
  }
}
