export default {
  template: require('./template'),

  computed: {
    approved() {return this.item.approved},
    updates_requested() {return this.item.update_requested_at && !this.item.review_requested_at},
    updated() {return this.item.review_requested_at && this.item.update_requests},
    is_new() {return this.approved || this.denied || this.updates_requested || this.updated ? false : true}
  }
}
