import Moment from 'moment'
import {height_format} from 'helpers/application_helpers'

export default {
  props: ['item'],
  template: require('./template'),

  methods: {
    height_format
  },

  filters: {
    birthday(user) {
      if (!user)
        return

      if (user.birth_year && user.birth_month && user.birth_day)
        return Moment([user.birth_year, user.birth_month, user.birth_day].join('-'), 'YYYY-M-D').format('MMMM D, YYYY')

      if (user.birth_year && user.birth_month)
        return Moment([user.birth_year, user.birth_month, 1].join('-'), 'YYYY-M-D').format('MMMM YYYY')

      if (user.birth_year)
        return user.birth_year
    }
  }
}
