import Status from '../status'
import member_status_map from '../../../member_status_map'
import member_type_map from '../../../member_type_map'

export default {
  props: ['item'],
  template: require('./template'),

  components: {
    Status
  },

  computed: {

    is_founder() {
      return this.item.member_type == 'founding' && this.item.founding_member_since
    },

    label() {
      return this.item.status == this.$options.approved_status ?
        member_type_map[this.item.member_type] :
        member_status_map[this.item.status]
    },

    waiting_for_password() {
      if (this.item.status !== 'approved')
        return false

      if (this.item.password_created_at)
        return false

      return true
    }
  }
}
