import Alert from 'appril-alert'

export default {
  props: ['item'],
  template: require('./template'),

  computed: {
    photo() {return this.$options.user_photos_url + '/' + this.item.feature_photo}
  },

  methods: {
    update_featured_bit(evt) {
      this.api.update_featured_bit(this.item.id, evt.target.checked).send({
        success() {Alert.success()}
      })
    }
  }
}
