class Models

  def delete_application_photo user_id, photo_id
    return unless photo = @user.application_photos.find_by(id: photo_id)
    photo.destroy
    true
  end


  def set_feature_photo user_id, photo_id
    return unless photo = @user.application_photos.find_by(id: photo_id)
    return unless File.file?(photo.original_path)
    @user.upload_photo('feature', Base64.encode64(File.read(photo.original_path)))
    @user.feature_photo
  end


  def rotate_application_photo user_id, photo_id, direction
    return unless photo = @user.application_photos.find_by(id: photo_id)
    photo.rotate(direction == 'right' ? 90 : -90)
    true
  end

end
