import Geo from 'appril-geo'
import Exporter from 'components/users/exporter'
import Emailer from 'components/users/bulk-emailer'

import member_status_map from '../member_status_map'
import member_type_map from '../member_type_map'

export default {
  template: require('./template'),

  data() { return {
    member_status_map,
    member_type_map,
  }},

  components: {
    Exporter,
    Emailer,
    Geo: Geo({
      template: require('./geo'),
      created() {
        this.api = this.env.components.Geo.rtcp
        this.countries = this.env.countries
        this.country = this.url.params.country
        this.state = this.url.params.state
        this.city = this.url.params.city
      }
    })
  }
}
