import * as computed from '../computed'

export default {
  props: ['product'],
  template: require('./template'),

  computed,

  methods: {
    set_color(evt) {
      this.$store.commit('ORDERS__SET_COLOR', {
        product: this.product,
        color: evt.target.options[evt.target.selectedIndex].value
      })
    }
  }
}
