import {scroll_top} from 'appril-utils'
import {product_photo} from 'components/orders/helpers'

export default {
  props: ['product'],
  template: require('./template'),

  computed: {
    photo() {
      return product_photo.call(this, this.product)
    }
  },

  methods: {
    open_product() {
      this.$store.commit('ORDERS__SET_PRODUCT', this.product)
      scroll_top()
    }
  }
}
