import {pixel} from 'components/orders/helpers'

export function name() {
  return [this.user.first_name, this.user.last_name].join(' ')
}

export function photo() {
  return this.user.profile_photo ?
    [this.env.user_photos_url, this.user.profile_photo].join('/') :
    pixel
}

export function edit_address() {
  return this.$store.state.Orders.edit_address
}
