export default {
  props: ['order'],
  template: require('./template'),

  computed: {
    billing_address() {
      return [
        this.order.billing_city,
        this.order.billing_state,
        this.order.billing_country == 'US' ? null : this.env.countries[this.order.billing_country]
      ].compact_join(', ')
    },

    shipping_address() {
      return [
        this.order.shipping_city,
        this.order.shipping_state,
        this.order.shipping_country == 'US' ? null : this.env.countries[this.order.shipping_country]
      ].compact_join(', ')
    }
  }
}
