import {map, reduce} from 'lodash'
import {trigger_create_sku} from '../methods'

import Sku from './sku'

export default {
  props: ['item'],
  template: require('./template'),

  components:{
    Sku
  },

  computed: {
    items_sold() {
      return reduce(map(this.item.skus, 'items_sold'), (f,c) => (f + c), 0)
    },

    items_in_stock() {
      reduce(map(this.item.skus, 'items_in_stock'), (f,c) => (f + c), 0)
    }
  },

  methods: {
    trigger_create_sku
  }
}
