import {reverse} from 'lodash'
import {update_inventory, close} from '../methods'

export default {
  template: require('./template'),

  computed: {
    additions() {
      return reverse(this.sku.additions || [])
    },

    subtractions() {
      return reverse(this.sku.subtractions || [])
    },

    sku(state) {return this.$store.state.Products.inventory_sku}
  },

  methods: {
    update_inventory,
    close
  }
}
