import Alert from 'appril-alert'

export default {
  props: ['item'],
  template: require('./template'),

  methods: {
    update_notes(evt) {
      let data = this.$el.querySelector('textarea[name="notes"]').value
      this.api.update_notes(this.item.id, data).send({
        success() {Alert.success()}
      })
    }
  }
}
