export function height_format(height) {
  if (!height)
    return

  let [feet, inches] = [parseInt(height), 0]

  while (!(feet % 12 === 0 || feet < 0)) {
    feet -= 1
    inches += 1
  }

  return `${feet / 12}&quot; ${inches}&apos;`
}
