import MediumEditor from 'medium-editor'
import 'medium-editor/dist/css/medium-editor.css'
import 'medium-editor/dist/css/themes/beagle.css'
import './medium-editor.css'

export default function (el, toolbar_el) {
  return new MediumEditor(el, {
    toolbar: {
      relativeContainer: toolbar_el,
      updateOnEmptySelection: true,
      buttons: [
        'bold',
        'italic',
        'underline',
        'strikethrough',
        'h1',
        'h2',
        'anchor',
        'justifyLeft',
        'justifyCenter',
        'justifyRight',
        'justifyFull',
        'outdent',
        'indent',
        'orderedlist',
        'unorderedlist',
        'quote',
        'removeFormat',
      ],
    },
    buttonLabels: 'fontawesome',
    paste: {
      forcePlainText: false
    },
  })
}
